// Optimized script - essential functions only
(function(){
'use strict';
const d=document,w=window;
function setupAuth(){
const rl=(e)=>{if(e)e.preventDefault();w.location.href='login.html'};
const bc=(e)=>{if(e)e.preventDefault();w.location.href=localStorage.getItem('userData')?'dashboard.html':'login.html'};
['login-btn','login-btn-nav'].forEach(id=>{const el=d.getElementById(id);if(el)el.onclick=rl});
['buy-btn','buy-btn-nav'].forEach(id=>{const el=d.getElementById(id);if(el)el.onclick=bc});
d.querySelectorAll('a[href="login.html"]').forEach(a=>a.onclick=(e)=>{e.preventDefault();w.location.href='login.html'});
}
d.querySelectorAll('a[href^="#"]').forEach(a=>a.onclick=function(e){e.preventDefault();const t=d.querySelector(this.getAttribute('href'));if(t)t.scrollIntoView({behavior:'smooth',block:'start'})});
const nav=d.querySelector('.navbar');
if(nav)w.addEventListener('scroll',()=>{nav.style.boxShadow=w.scrollY>100?'0 4px 20px rgba(0,0,0,0.1)':'none'});
d.querySelectorAll('.faq-question').forEach(q=>q.onclick=function(){const i=this.parentElement,a=i.classList.contains('active');d.querySelectorAll('.faq-item').forEach(x=>x.classList.remove('active'));if(!a)i.classList.add('active')});
if(d.readyState==='loading')d.addEventListener('DOMContentLoaded',setupAuth);else setupAuth();
setTimeout(setupAuth,100);
})();

